import java.util.*;
public class ProductRevenue {
 public static void main(String[] args) {
 // create the table of products and their information
 String[][] products = {
 {"Product A", "100", "10.50"},
 {"Product B", "50", "25.00"},
 {"Product C", "75", "15.75"},
 {"Product D", "200", "5.00"},
 {"Product E", "150", "12.25"}
 };

 // create a HashMap to store the revenue information
 HashMap revenueMap = new HashMap();

 // loop through the table to compute the revenue for each product
 for (int i = 0; i < products.length; i++) {
 String productName = products[i][0];
 int unitsSold = Integer.parseInt(products[i][1]);
 double pricePerUnit = Double.parseDouble(products[i][2]);
 double revenue = unitsSold * pricePerUnit;

 // add the revenue information to the HashMap
 revenueMap.put(productName, revenue);
 }

 // sort the HashMap by value in descending order
 List> sortedList = new ArrayList>(revenueMap.entrySet());
 Collections.sort(sortedList, new Comparator>() {
 public int compare(Map.Entry o1, Map.Entry o2) {
 return (o2.getValue()).compareTo(o1.getValue());
 }
 });

 // print out the products in order of most to least revenue
